            that.$header = that.$wrapper.find(".t-elastic-header");

            // VARS
            that.wrapper_offset = that.$wrapper.offset();
            that.header_w = that.$header.outerWidth();
            that.header_h = that.$header.outerHeight();
            that.fixed_class = "is-fixed";

            // DYNAMIC VARS
